DROP TABLE IF EXISTS user,csv_file,question;
CREATE TABLE user (id INTEGER PRIMARY KEY AUTO_INCREMENT,username VARCHAR(255) UNIQUE NOT NULL,password VARCHAR(255) NOT NULL);
CREATE TABLE csv_file (id INTEGER PRIMARY KEY AUTO_INCREMENT,user_id INTEGER NOT NULL,uploaded TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,file_name VARCHAR(255) NOT NULL,file_path VARCHAR(255) NOT NULL,FOREIGN KEY (user_id) REFERENCES user (id));
CREATE TABLE question (id INTEGER PRIMARY KEY AUTO_INCREMENT,req_time TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,question VARCHAR(255) NOT NULL,response VARCHAR(255),file_id INTEGER NOT NULL,FOREIGN KEY (file_id) REFERENCES csv_file (id));